  `image` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '广告图片id',
  `src` varchar(255) NOT NULL DEFAULT '' COMMENT '广告图片路径',
  `link` varchar(255) NOT NULL DEFAULT '' COMMENT '广告链接地址',
